RegistrationSettings.GenerateUsernameScript:
//log("warning", JSON.stringify(context));
//log("warning", context.loginProvider);

if(context.loginProvider === "GitHub") 
{
  var claim = context.externalClaims.find(c => c.type == "name");
  if(claim.value)
  {
    let generatedUsername = claim.value;
    if (generatedUsername.includes("@");
	{
		generatedUsername = generatedUsername.split("@")[0];
    }
    context.userName = generatedUsername.toLowerCase();
    return;
  }
}
log("warning", "Provider " + context.loginProvider + " was not handled");

