OnValidation.Text:
var data = requestFormAsJsonObject();
var localizedText = getLocalizedTextValues(getFormContentItem());

if(!isAuthenticated() || data.title == "" || data.statement == "" || data.datasets == "" || data.backgroundInfo == "" || data.organizationNameEn == "" || data.organizationAcronymEn == "" || data.contactPersonName == "" || data.contactPersonEmail == "" || data.technicalMentorName == "" || data.technicalMentorEmail == "" || data.caseSpecialistMentorName == "" || data.caseSpecialistMentorEmail == "") {
  addError('serverValidationMessage', localizedText.serverValidationError);
}

OnSubmitted.Text:
var data = requestFormAsJsonObject();
var localizedText = getLocalizedTextValues(getFormContentItem());
var saveMediaResults = saveMedia("challenge", true);
var datasetsFiles = getMediaPaths(saveMediaResults);

if(saveMediaResults[0]?.hasError == true){
    addError("serverValidationMessage", localizedText.mediaUploadError);
    return;
}

if(data.datasetsFilesOld != null){
	if(Object.keys(data.datasetsFilesOld).length > 10){
    	for(var path of data.datasetsFilesOld){
        	datasetsFiles.push(path[1]);
    	}
	} else {
    	datasetsFiles.push(data.datasetsFilesOld);
	}
}
  
if (data.challengeId == ""){
var item = createContentItem("Challenge", false, { 
  "Challenge": {
    "Title": {
      "Text": data.title
    },
    "Statement": {
      "Text": data.statement
    },
    "Datasets": {
      "Text": data.datasets
    },
    "DatasetsFiles": {
      "Paths": datasetsFiles
    },
    "BackgroundInformation": {
      "Text": data.backgroundInfo
    },
    "Keywords": {
      "Text": data.keywords
    },
    "OrganizationNameEn": {
      "Text": data.organizationNameEn
    },
    "OrganizationAcronymEn": {
      "Text": data.organizationAcronymEn
    },
    "ContactPersonName": {
      "Text": data.contactPersonName
    },
    "ContactPersonEmail": {
      "Text": data.contactPersonEmail
    },
    "TechnicalMentorName": {
      "Text": data.technicalMentorName
    },
    "TechnicalMentorEmail": {
      "Text": data.technicalMentorEmail
    },
    "CaseSpecialistName": {
      "Text": data.caseSpecialistMentorName
    },
    "CaseSpecialistEmail": {
      "Text": data.caseSpecialistMentorEmail
    },
    "Comments": {
      "Text": data.comments
    }
  }
});
} else {
 var update = updateContentItem(contentByVersionId(data.challengeId), {
  "Challenge": {
    "Title": {
      "Text": data.title
    },
    "Statement": {
      "Text": data.statement
    },
    "Datasets": {
      "Text": data.datasets
    },
    "DatasetsFiles": {
      "Paths": datasetsFiles
    },
    "BackgroundInformation": {
      "Text": data.backgroundInfo
    },
    "Keywords": {
      "Text": data.keywords
    },
    "OrganizationNameEn": {
      "Text": data.organizationNameEn
    },
    "OrganizationAcronymEn": {
      "Text": data.organizationAcronymEn
    },
    "ContactPersonName": {
      "Text": data.contactPersonName
    },
    "ContactPersonEmail": {
      "Text": data.contactPersonEmail
    },
    "TechnicalMentorName": {
      "Text": data.technicalMentorName
    },
    "TechnicalMentorEmail": {
      "Text": data.technicalMentorEmail
    },
    "CaseSpecialistName": {
      "Text": data.caseSpecialistMentorName
    },
    "CaseSpecialistEmail": {
      "Text": data.caseSpecialistMentorEmail
    },
    "Comments": {
      "Text": data.comments
    }
  }
}); 
}

ComponentOptions.Text:
{% assign challengeId = Request.Query['id'] %}
{% assign challenge =  Content.ContentItemVersionId[challengeId] %}

{
  data: () => ({
    title: "{{ challenge.ContentItem.Content.Challenge.Title.Text | js_encode }}",
	statement: "{{ challenge.ContentItem.Content.Challenge.Statement.Text | js_encode }}", 
    datasets: "{{ challenge.ContentItem.Content.Challenge.Datasets.Text | js_encode }}",
    datasetsFilesOld: [ 
  	  {%- for file in challenge.ContentItem.Content.Challenge.DatasetsFiles.Paths -%}
		'{{ file }}',
	  {%- endfor -%}
 	],
    backgroundInfo: "{{ challenge.ContentItem.Content.Challenge.BackgroundInformation.Text | js_encode }}",
    keywords: "{{ challenge.ContentItem.Content.Challenge.Keywords.Text | js_encode }}",
    organizationNameEn: "{{ challenge.ContentItem.Content.Challenge.OrganizationNameEn.Text | js_encode }}",
    organizationAcronymEn: "{{ challenge.ContentItem.Content.Challenge.OrganizationAcronymEn.Text | js_encode }}",
    contactPersonName: "{{ challenge.ContentItem.Content.Challenge.ContactPersonName.Text | js_encode }}",
    contactPersonEmail: "{{ challenge.ContentItem.Content.Challenge.ContactPersonEmail.Text | js_encode }}",
    technicalMentorName: "{{ challenge.ContentItem.Content.Challenge.TechnicalMentorName.Text | js_encode }}",
    technicalMentorEmail: "{{ challenge.ContentItem.Content.Challenge.TechnicalMentorEmail.Text | js_encode }}",
    caseSpecialistMentorName: "{{ challenge.ContentItem.Content.Challenge.CaseSpecialistName.Text | js_encode }}",
    caseSpecialistMentorEmail: "{{ challenge.ContentItem.Content.Challenge.CaseSpecialistEmail.Text | js_encode }}",
    comments: "{{ challenge.ContentItem.Content.Challenge.Comments.Text | js_encode }}",
    counter: 1,
    challengeId: "{{ challengeId }}"
  })
}
