OnValidation.Text:
var data = requestFormAsJsonObject();
var localizedText = getLocalizedTextValues(getFormContentItem());

if(data.score1 == "" || data.score2 == "" || data.score3 == "") {
  addError('serverValidationMessage', localizedText.scoreRequiredError);
}

OnSubmitted.Text:
var data = requestFormAsJsonObject();

var item = createContentItem("Score", true, {
  "Score": {
    "Score": {
      "Value": parseInt(data.score1) + parseInt(data.score2) + parseInt(data.score3)
    },
    "Round": {
      "Value": data.round
    },
    "Judge": {
      "UserIds": [
        data.judge
      ]
    },
    "Team": {
      "ContentItemIds": [
        data.team
      ]
    },
  },
  "JudgeType": {
    "Type": {
      "Values": [
        data.judgeType
      ]
    }
  }
});

ComponentOptions.Text:
{% assign userId = User | user_id %}
{% assign user = User | user_id | users_by_id %}
{% assign round = Site.Properties.HackathonCustomSettings.JudgingCustomSettings.IsFinalRound.Value %}
{% assign judgeType = user.Properties.Judge.JudgeType.Type.Values | first %}

{
  data: () => ({
    score1: "",
    score2: "",
    score3: "",
    judge: "{{ userId }}",
    team: "{{ Request.Query['teamId'] }}",
    round: {% if round %}"2"{% else %}"1"{% endif %},
    judgeType: "{{ judgeType }}"
  })
}
