ComponentOptions.Text:
{% assign user = User | user_id | users_by_id %}
{% assign teamContentItemId = user.Properties.Hacker.Hacker.Team.ContentItemIds | first %}
{% assign team =  Content.ContentItemId[teamContentItemId] %}
{% assign topicList = Queries.GetItemsForHackathon | query: type: "Topic" %}

{
  data: () => ({
    teamName: "{{ team.Content.Team.Name.Text | js_encode }}",
	teamDescription: "{{ team.Content.Team.Description.Text | js_encode }}", 
    topics: [
        {% if team.Content.Team.Topics.ContentItemIds != null %}
            {% for topic in team.Content.Team.Topics.ContentItemIds %}
                {% assign topicItem =  Content.ContentItemId[topic] %}
                {% if Culture.Name == "en" %}
                    { name: "{{topicItem.Content.Topic.NameEn.Text | js_encode }}", id: "{{topicItem.ContentItemId}}" },
                {% else %}
                    { name: "{{topicItem.Content.Topic.NameFr.Text | js_encode }}", id: "{{topicItem.ContentItemId}}" },
                {% endif %}
            {% endfor %}
        {% else %}
        {% for topic in topicList %}
            {% if Culture.Name == "en" %}
                { name: "{{topic.Content.Topic.NameEn.Text | js_encode }}", id: "{{topic.ContentItemId}}" },
            {% else %}
                { name: "{{topic.Content.Topic.NameFr.Text | js_encode }}", id: "{{topic.ContentItemId}}" },
            {% endif %}           
        {% endfor %}
        {% endif %}
    ],
    teamId: "{{ team.ContentItemVersionId }}"
  })
}

OnValidation.Text:
var data = requestFormAsJsonObject();
var localizedText = getLocalizedTextValues(getFormContentItem());

if(!isAuthenticated() || data.teamName == "" || data.teamDescription == "") {
  addError('serverValidationMessage', localizedText.serverValidationMessage);
}

OnSubmitted.Text:
var data = requestFormAsJsonObject();
var localizedText = getLocalizedTextValues(getFormContentItem());

var updateResult = updateContentItem(contentByVersionId(data.teamId), {
  "Team" : {
     "Name": {
       "Text": data.teamName
     },
     "Description": {
       "Text": data.teamDescription
     },
     "Topics": {
       "ContentItemIds": data["topics[id]"]
     }
  }
});

if(updateResult == 1 || updateResult == 2 ) 
{
  addError("serverValidationMessage", localizedText.updateSettingsError);  
}
