var data = requestFormAsJsonObject();
var localizedText = getLocalizedTextValues(getFormContentItem());

var contentItem = getLocalizedContentItemById(data.id);
var contentFormModel = convertToFormModel("Proposal", data);

if(!validateRadarEntityName("Proposal", contentFormModel.Name, contentItem))
{
    addError('serverValidationMessage', localizedText.nameNonUniqueError);
}

if(!validateMaxStringLength(contentFormModel.Name, 100))
{
    addError('serverValidationMessage', localizedText.nameLengthError);
}

if(!validateMaxStringLength(contentFormModel.Description, 100))
{
    addError('serverValidationMessage', localizedText.descriptionLengthError);
}

if(!validateString(contentFormModel.Name) || !validateString(contentFormModel.Description))
{
    addError('serverValidationMessage', localizedText.emptyTextError);
}

if(!validateString(contentFormModel.Name))
{
    addError('serverValidationMessage', localizedText.nameEmptyError);
}

if(!validateString(contentFormModel.Description))
{
    addError('serverValidationMessage', localizedText.descriptionEmptyError);
}

if(!validateTopics(contentFormModel.Topics))
{
    addError('serverValidationMessage', localizedText.topicError);
}

if(!validateRelatedEntities(contentFormModel.RelatedEntities, "Community"))
{
    addError('serverValidationMessage', localizedText.communityError);
}

if(!validateEntityType("Proposal Types", contentFormModel.Type))
{
    addError('serverValidateMessage', localizedText.typeError);
}

OnSubmitted.Text:
var data = requestFormAsJsonObject();

var contentFormModel = convertToFormModel("Proposal", data);
var contentDocument = convertToContentDocument("Proposal", contentFormModel);

// If existing content can't be found then default to create
var contentItem = getLocalizedContentItemById(data.id);

if (contentItem == null) {
    contentItem = createLocalizedContentItem("Proposal", contentDocument);
} else {
    updateLocalizedContentItem(contentItem, contentDocument);
}

httpRedirect(`~/proposals/${contentItem.ContentItemId}`);

OnValidation.Text:
var data = requestFormAsJsonObject();
var localizedText = getLocalizedTextValues(getFormContentItem());

var contentFormModel = convertToFormModel("Artifact", data);

var parentContentItem = getLocalizedContentItemById(data.parentId);

if(parentContentItem == null)
{
    addError('serverValidationMessage', localizedText.noParentError);
}

if(!validateUrl(contentFormModel.Url, "www.github.com"))
{
    addError('serverValidationMessage', localizedText.urlError);
}

OnSubmitted.Text:
var data = requestFormAsJsonObject();

var contentFormModel = convertToFormModel("Artifact", data);

var id = createOrUpdateArtifact(data.parentId, data.id, contentFormModel);

if(id !== null)
{
    var parentContentItem = getLocalizedContentItemById(data.parentId);

    if(parentContentItem != null)
    {
        if(parentContentItem.ContentType == "Project")
        {
            httpRedirect(`~/projects/${parentContentItem.ContentItemId}/artifacts/${id}`);
        } else if(parentContentItem.ContentType == "Event")
        {
            httpRedirect(`~/events/${parentContentItem.ContentItemId}/artifacts/${id}`);
        } else if(parentContentItem.ContentType == "Proposal")
        {
            httpRedirect(`~/prosals/${parentContentItem.ContentItemId}/artifacts/${id}`);
        }
    }
}

