LiquidPart.Liquid:
{% assign topics = Queries.AllTaxonomiesSQL | query: type: "Topics" | first %}
{% assign trending_up = Queries.TopicTrendingUpSQL | query %}
{% assign trending_down = Queries.TopicTrendingDownSQL | query %}

{% assign entities = Queries.AllTaxonomiesSQL | query: type: "Topics" | first %}
{% assign trending_up = Queries.TopicTrendingUpSQL | query %}
{% assign trending_down = Queries.TopicTrendingDownSQL | query %}

<div class="card-body">
  <div class="trend-card-body mt-5">
    <div class="trend-container">
      <v-icon color="#009c88" style="font-size: 145px;">
        mdi-trending-up
      </v-icon>
      <div class="ml-5">
        {% for tup in trending_up %}
        {% assign e = topics.ContentItem.Content.TaxonomyPart.Terms | where: "ContentItemId", tup.TermContentItemId | first %}
        <div class="mb-2">
          {{ e | shape_build_display: "Summary" | shape_render }}
        </div>
        {% endfor %}
      </div>
    </div>
    <div class="trend-down">
      <v-icon color="#009c88" style="font-size: 145px;">
        mdi-trending-down
      </v-icon>
    </div>
  </div>
</div>
<a href="{{ topics | display_url }}" class="black--text card-button-link">
  <v-btn elevation="0" text class="card-button">
    All Topics
  </v-btn>
</a>

{% assign proposals = Queries.AllEntitiesSQL | query: contentType: "Proposal" %}
<div class="table-container">
  <table class="table" id="entity-table">
    <thead class="table-header">
      <tr>
        <th class="name-col">Name</th>
        <th>Created</th>
        <th>Visibility</th>
      </tr>
    </thead>
    <tbody>
      {% for proposal in proposals %}
      <tr class="table-row">
        <td class="name-col">{{ proposal | display_text }}</td>
        <td class="table-col">
          {{ proposal.CreatedUtc | local | date: "%b %d, %Y" }}
        </td>
        <td class="table-col">
          {% if proposal.Published %}
          	Published
          {% else %}
          	Draft
          {% endif %}
        </td>
      </tr>
      {% endfor %}
    </tbody>
  </table>
</div>

<script type="application/javascript">
function search() {
    var input, filter, table, tr, td, i, txtValue;
    input = document.getElementById("search-input");
    filter = input.value.toLowerCase();
    table = document.getElementById("entity-table");
    tr = table.getElementsByTagName("tr");

    for (i = 0; i < tr.length; i++) {
        td = tr[i].getElementsByTagName("td")[0]; // Filter based on the name
        if (td) {
            txtValue = td.textContent || td.innerText;
            if (txtValue.toLowerCase().indexOf(filter) > -1) {
                tr[i].style.display = "";
            } else {
                tr[i].style.display = "none";
            }
        }
    }
}
</script>

