{% assign events = Queries.AllEntitiesSQL | query: contentType: "Event" %}
<div class="table-container">
  <table class="table" id="entity-table">
    <thead class="table-header">
      <tr>
        <th class="name-col">Name</th>
        <th>Date</th>
        <th>Organizer</th>
        <th>Visibility</th>
      </tr>
    </thead>
    <tbody>
      {% for event in events %}
      <tr class="table-row">
        <td class="name-col">{{ event | display_text }}</td>
        <td class="table-col">
