ClientInit.Text:
VeeValidate.setInteractionMode('passive');
VeeValidate.localize({
  {{Culture.Name}}: {
    fields: {
      name: {
        required: '{{ "nameRequiredError" | localize }}',
      },
      shortDescription: {
        required: '{{ "shortDescriptionRequiredError" | localize }}'
      },
      description: {
        required: '{{ "descriptionRequiredError" | localize }}' 
      }
    }
  }
});
return new Vuetify();

OnValidation.Text:
var data = requestFormAsJsonObject();
var localizedText = getLocalizedTextValues(getFormContentItem());

if(data.name == "") {
  addError('name', localizedText.nameRequiredError);
}

if(data.shortDescription == "") {
  addError('shortDescription', localizedText.shortDescriptionRequiredError);
}

if(data.description == "") {
  addError('description', localizedText.descriptionRequiredError);
}

OnSubmitted.Text:
var data = requestFormAsJsonObject();

var item = createContentItem("Challenge", false, {
  "Challenge": {
    "Name": {
      "Text": data.name
    },
    "ShortDescription": {
      "Text": data.shortDescription
    },
    "Description": {
      "Html": data.description
    }
  }
});

httpRedirect('');
