OnValidation.Text:
var data = requestFormAsJsonObject();
var localizedText = getLocalizedTextValues(getFormContentItem());

if(data.name == "" || data.shortDescription == "" || data.description == "") {
  addError('serverValidationMessage', localizedText.serverValidationError);
}

OnSubmitted.Text:
var data = requestFormAsJsonObject();

var item = createContentItem("Challenge", false, {
  "Challenge": {
    "Name": {
      "Text": data.name
    },
    "ShortDescription": {
      "Text": data.shortDescription
    }
  },
  "MarkdownBodyPart": {
    "Markdown": data.description
  }
});
