ClientInit.Text:
VeeValidate.setInteractionMode('passive');
VeeValidate.localize({
  {{Culture.Name}}: {
    fields: {
      comment: {
        required: '{{ "scoreRequiredError" | localize }}',
      }
    }
  }
});
return new Vuetify();

OnValidation.Text:
var data = requestFormAsJsonObject();
var localizedText = getLocalizedTextValues(getFormContentItem());

if(data.score == "" && data.comment == "") {
  addError('score', localizedText.scoreRequiredError);
}

OnSubmitted.Text:
var data = requestFormAsJsonObject();

var item = createContentItem("ScoreEntry", true, {
  "Score": {
    "Score": {
      "Value": data.score
    },
    "Judge": {
      "ContentItemIds": [
        data.judge
      ]
    },
    "Team": {
      "ContentItemIds": [
        data.team
      ]
    },
    "comment": {
      "Text": data.comment
    }
  }
});

httpRedirect('hackpage');
