OnValidation.Text:
var data = requestFormAsJsonObject();
var localizedText = getLocalizedTextValues(getFormContentItem());

if(data.otherFieldsOfInterest == "other" && data.otherInterest == "" || 
    data.otherFieldsOfExpertise == "other" && data.otherExpertise == "" || 
    data.citizenship == "no" && data.citizenshipStatus == "" ) 
 {
  addError('serverValidationMessage', localizedText.serverValidationError);
 } else if(data.degreesCompleted == "") {
  addError("serverValidationMessage", localizedText.degreesCompletedValidation); 
} else if(data.gcJobInterest == "") {
  addError("serverValidationMessage", localizedText.gcJobInterestValidation); 
} else if(data.jobType == "") {
  addError("serverValidationMessage", localizedText.jobTypeValidation); 
} else if(data.coop == "") {
  addError("serverValidationMessage", localizedText.coopValidation); 
} else if(data.fswep == "") {
  addError("serverValidationMessage", localizedText.fswepValidation); 
} else if(data.location == "") {
  addError("serverValidationMessage", localizedText.locationValidation); 
} else if(data.citizenship == "") {
  addError("serverValidationMessage", localizedText.citizenshipValidation); 
} else if(data.language == "") {
  addError("serverValidationMessage", localizedText.languageValidation); 
} else if(
    data.sql == "" || 
    data.sas == ""  || 
    data.r == ""  || 
    data.python == ""  || 
    data.java == ""  || 
    data.perl == ""  || 
    data.scala == ""  || 
    data.julia == ""  || 
    data.matlab == ""  || 
    data.stata == ""  || 
    data.powerBi == ""  || 
    data.qgis == ""  || 
    data.arcgis == ""  || 
    data.grass == ""  || 
    data.cPlusPlus == ""  || 
    data.cSharp == ""  || 
    data.javascript == ""  || 
    data.html == ""  || 
    data.css == "" ) {
  addError("serverValidationMessage", localizedText.programmingLanguagesValidation); 
} else if(data.experience == "") {
  addError("serverValidationMessage", localizedText.experienceValidation); 
} else if(data.otherLanguage != "" & data.otherLanguageRating == "") {
    addError("serverValidationMessage", localizedText.otherLanguageRatingValidation); 
}

if(hasErrors()) 
{
  return; 
}

