href.Content:
{% assign url = Content | href -%}
{% if args.returnUrl -%}
	{{ url | return_url }}
{% else -%}
	{{ url }}
{% endif -%}

col.Content:
{%- capture col -%}{%- if Args[0] != blank %} col-{{ Args[0] }}{%- else -%}col{%- endif -%}{%- endcapture -%}
{%- capture colsm -%}{%- if Args['sm'] != blank %} col-sm-{{ Args['sm'] }}{%- endif -%}{%- endcapture -%}
{%- capture colmd -%}{%- if Args['md'] != blank %} col-md-{{ Args['md'] }}{%- endif -%}{%- endcapture -%}
{%- capture collg -%}{%- if Args['lg'] != blank %} col-lg-{{ Args['lg'] }}{%- endif -%}{%- endcapture -%}
{%- capture colxl -%}{%- if Args['xl'] != blank %} col-xl-{{ Args['xl'] }}{%- endif %}{%- endcapture -%}
{%- capture class -%}{%- if Args['class'] != blank %}{{ Args['class'] }}{%- endif -%}{%- endcapture -%}

<div class="{{ class }}{{col}}{{colsm}}{{colmd}}{{collg}}{{colxl}}">{{ Content | raw }}</div>

HtmlBodyPartTrumbowygEditorSettings.Options:
{
    autogrow: true,
    btns: [
        ["viewHTML"],
        ["undo", "redo"],
        ["formatting"],
        ["strong", "em", "del"],
        ["foreColor", "backColor"],
        ["superscript", "subscript"],
        ["link"],
        ["image"],
        ["align"],
        ["unorderedList", "orderedList"],
        ["horizontalRule"],
        ["removeformat"],
        ["fullscreen"]
    ],
    btnsDef: {
        align: {
            dropdown: ["justifyLeft", "justifyCenter", "justifyRight", "justifyFull"],
            ico: "justifyLeft"
        },
        image: {
            dropdown: ["insertImage", "base64", "noembed"],
            ico: "insertImage"
        }
    }
}
