vcol.Content:
{% if Content == nil %}
<v-col>empty</v-col>
{% else %}
<v-col cols="{{ Args.Text }}">{{ Content | sanitize | raw }}</v-col>
{% endif %}

vrow.Content:
{% if Content == nil %}
<v-row>empty</v-row>
{% else %}
<v-row align-content="{{ Args.Text }}">{{ Content | sanitize | raw }}</v-row>
{% endif %}

vcontainer.Content:
{% if Content == nil %}
<v-container>empty</v-container>
{% else %}
<v-container {{Args.Text}}>{{ Content | sanitize | raw }}</v-container>
{% endif %}
