vcol.Content:
{% if Args.Text != nil %}
<v-col>{{Args.Text | sanitize | raw }}</v-col>
{% else %}
<v-col>{{ Content | sanitize | raw }}</v-col>
{% endif %}

vrow.Content:
{% if Args.Text != nil %}
<v-row>{{Args.Text}}</v-row>
{% else %}
<v-row>{{Content | sanitize | raw }}</v-row>
{% endif %}

vcontainer.Content:
{% if Args.Text != nil %}
<v-container>{{Args.Text}}</v-container>
{% else %}
<v-container>{{ Content | sanitize | raw }}</v-container>
{% endif %}
