{
  data: () => ({
    items: ["Female", "Male", "Other"],
    name: "",
    email: "",
    gender: undefined
  })
}

OnValidation.Text:
var data = requestFormAsJsonObject();
var strings = getLocalizedTextValues(getFormContentItem());

if(data.name == "") {
 addError('name', strings.nameVal); 
}

Script.Text:
{
  data: () => ({
    items: ["Female", "Male", "Other"],
    name: "",
    email: "",
    gender: undefined
  })
}

OnValidation.Text:
var data = requestFormAsJsonObject();
var strings = getLocalizedTextValues(getFormContentItem());

if(data.name == "") {
 addError('name', strings.nameVal); 
}

OnSubmitted.Text:
var data = requestFormAsJsonObject();

var item = createContentItem("PersonInfo", true, {
  "PersonInfo": {
    "Name": {
      "Text": data.name
    },
    "Email": {
      "Text": data.email
    },
    "Gender": {
      "Text": data.gender
    }
  }
});

httpRedirect('Contents/ContentItems/' + item.ContentItemId);
