Script.Text:
{
  data: () => ({
    items: ["Female", "Male", "Other"],
    name: "",
    email: "",
    gender: undefined
  })
}

ClientInit.Text:
VeeValidate.setInteractionMode('passive');

return new Vuetify({
  theme: {
    dark: true,
  },
});

OnValidation.Text:
var data = requestFormAsJsonObject();
var strings = getLocalizedTextValues(getFormContentItem());

if(data.name == "") {
 addError('name', strings.nameVal); 
}

OnSubmitted.Text:
var data = requestFormAsJsonObject();

createContentItem("PersonInfo", true, {
  "PersonInfo": {
    "Name": {
      "Text": data.name
    },
    "Email": {
      "Text": data.email
    },
    "Gender": {
      "Text": data.gender
    }
  }
});

httpContext().Response.Redirect('/success')
